create table item (id integer generated by default as identity, description varchar(400), name varchar(255) not null, price double not null, url varchar(255) not null, order_id varchar(255), primary key (id));
create table orderinfo (id varchar(255) not null, delivery_date date, last_update_ts timestamp,delivery_time varchar(255), status varchar(255), client_name varchar(255), delivery_address varchar(255), payment_method varchar(100), contact_phone varchar(30), carrier varchar(150), in_transit_date timestamp, channel varchar(100), primary key (id));